package com.flaviu.clearscoretestapp.uiimport android.os.Bundleimport androidx.activity.compose.setContentimport androidx.activity.viewModelsimport androidx.appcompat.app.AppCompatActivityimport com.flaviu.clearscoretestapp.ui.CreditDetailsActivity.Companion.getStartIntentimport com.flaviu.clearscoretestapp.ui.composables.Errorimport com.flaviu.clearscoretestapp.ui.model.MainViewModelimport com.flaviu.clearscoretestapp.utils.DataStateimport dagger.hilt.android.AndroidEntryPoint@AndroidEntryPointclass CreditScoreActivity : AppCompatActivity() {    private val viewModel: MainViewModel by viewModels()    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        viewModel.dataViewState.observe(this, { viewState -> renderViewState(viewState) })        viewModel.fetchCreditScore()    }    private fun renderViewState(viewState: DataState) {        when (viewState) {            is DataState.Loading -> {                setContent {                    DataState.Loading                }            }            is DataState.Error -> {                setContent {                    Error() { finish() }                }            }            is DataState.CreditScoreLoaded -> {                setContent {                    viewState.creditScore?.let {                        com.flaviu.clearscoretestapp.ui.composables.MainLayout(                            creditScore = viewState.creditScore                        ) {                            startActivity(                                getStartIntent(                                    this,                                    viewState.creditScore                                )                            )                        }                    }                }            }        }    }}